import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Header from './Header';
// import Navigationvar from './Navigationvar';
// import { formatDate } from '../utils/helpers'; // Import helper function for date formatting
// import female from '../assets/pngtree-female-user-avatars-flat-style-women-profession-vector-png-image_1529171.jpg';
// import male from '../assets/pngtree-user-vector-avatar-png-image_1541962.jpg';
// import './ViewPatient.css'; // Import CSS file for styling

export default function Profile() {
    function formatDate(inputDate){
        const date=new Date(inputDate);
        const day=String(date.getDate()).padStart(2,'0');
        const month= String(date.getMonth()+1).padStart(2,'0');
        const year=date.getFullYear();
        return `${day}/${month}/${year}`
    }
  const { id } = useParams();
  const [file, setFile] = useState("");
  const [patientDetails, setPatientDetails] = useState(null);
  const [fileName, setFileName] = useState("");
  const [size, setSize] = useState(0);
  const [img, setImg] = useState("");
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchPatientDetails = async () => {
      try {
        const res = await axios.get(`http://loca/en/patientdetail?id=${id}`);
        setPatientDetails(res.data[0]);

        // if (res.data[0].gender === "female") {
        //   setImg(female);
        // } else if (res.data[0].gender === "male") {
        //   setImg(male);
        // }
      } catch (error) {
        console.error("Error occurred:", error);
      }
    };

    fetchPatientDetails();
  }, [id]);

  const handleFileClick = async (file) => {
    navigate(`/report/${file}`);
  };

  const handleFile = async (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = async () => {
        const fileData = reader.result.split(',')[1]; // Get base64-encoded file data
        const filename = event.target.value.replace("C:\\fakepath\\", "");

        try {
          console.log("hi");
          const response = await axios.post('http://localhost:3001/en/uploadpdf', { file: fileData, filename: filename, patientId: id, name: patientDetails.name });

          console.log("submitted successfully");
        } catch (error) {
          console.log("Error uploading details:", error);
          alert('File size too large or other issues.');
        }
      };
      reader.readAsDataURL(selectedFile);
    } else {
      console.log("No input file");
    }
  };

  return (
    <div className="view-patient-container">
        <Header/>
    
      <div className="content">
        <div className="header">
          <button className="patient-name">
            {patientDetails ? patientDetails.name : "Loading..."}
          </button>
          <button className="book-appointment">
            Book Appointment
          </button>
        </div>

        {patientDetails && (
          <div className="profile-container">
            <div className="profile-details">
              <h3>Patient Profile</h3>
              <img src={img} className="avatar" alt="Patient" />
              <p className="info-label">Contact Details:</p>
              <p className="info-value">{patientDetails.name}</p>
              <p className="info-value">{patientDetails.gender}</p>
              <p className="info-value">DOB: {formatDate(patientDetails.DOB)}</p>
              <p className="info-value">CHRONICS: {patientDetails.chronics.join(', ')}</p>
            </div>
            <div className="lab-results">
              <h3>Latest Lab Results</h3>
              <div>
                {patientDetails.reportsList && patientDetails.reportsList.map((file, index) => (
                  <div key={index} className="file-item" onClick={() => handleFileClick(file)}>
                    <i className="fas fa-file-alt file-icon" />
                    <p>File ID: {file}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        <div className="upload-section">
          <h3>Upload New Report</h3>
          <label
            htmlFor="file-upload"
            className="upload-button"
          >
            Upload Reports
          </label>
          <input
            id="file-upload"
            type="file"
            className="hidden"
            onChange={handleFile}
          />
        </div>
      </div>
    </div>
  );
}
