import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import jsPDF from 'jspdf'; // Import jspdf library
import 'jspdf-autotable'; // Import autotable plugin
// import Navigationvar from './Navigationvar';
import Header from './Header';


export default function Analysis() {
  const [patient, setPatient] = useState(null);
  
  const { id } = useParams();
  

  useEffect(() => {
    const getReportDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/en/getreportdetails?id=${id}`);
        setPatient(response.data);
      } catch (error) {
        console.error('Error fetching report details:', error);
      }
    };

    getReportDetails();
  }, [id]);
  const generatePDF = () => {
    if (!patient) {
      console.error('Patient data is not loaded yet.');
      return;
    }

    const doc = new jsPDF();

    // Add report details to the PDF
    doc.setFontSize(18);
  doc.text('SAANJH SAHAYAK', doc.internal.pageSize.width / 2, 10, { align: 'center' });
    doc.setFontSize(16);
    doc.text('Report Details', 10, 20);

    doc.setFontSize(12);
    doc.text(`ID: ${patient._id}`, 10, 35);
    doc.text(`Date of Report: ${patient.dateOfReport ? new Date(patient.dateOfReport).toLocaleDateString() : 'N/A'}`, 10, 45);
    doc.text(`Patient: ${patient.patient}`, 10, 55);

    // Split summary text into lines that fit within the page width
    const summaryLines = doc.splitTextToSize(patient.summary || '', doc.internal.pageSize.width - 20);
    doc.text('Summary:', 10, 65);
    summaryLines.forEach((line, index) => {
      if (index < 5) { // Limit to show only 5 lines of summary
        doc.text(line, 15, 75 + index * 10); // Adjust Y position based on index and line height
      }
    });

    doc.text(`Severity: ${patient.severity}`, 10, 135);
    doc.text(`Specialist Required: ${patient.specialistReq}`, 10, 145);
    let yPos = 155;

    // Add precautions
    if (patient.precautions.length > 0) {
      doc.text('Precautions:', 10, yPos);
      yPos += 10;
      patient.precautions.forEach((precaution, index) => {
        yPos += 5; // Add spacing between each precaution
        doc.text(`- ${precaution}`, 15, yPos + index * 10);
      });
      yPos += (patient.precautions.length * 10) + 10; // Add extra space after precautions
    }
  
    // Add doctor's note
    if (patient.doctorNotes) {
      doc.text('Doctor\'s Note:', 10, yPos);
      yPos += 10;
      const doctorNoteLines = doc.splitTextToSize(patient.doctorNotes, doc.internal.pageSize.width - 20);
      doctorNoteLines.forEach((line, index) => {
        doc.text(line, 15, yPos + index * 10);
      });
    }

    // Save the PDF
    doc.save(`Report-${patient._id}.pdf`);
  };
 

  const handlePDFView = async () => {
    try {
      
      const response = await axios.get(`http://localhost:3001/en/files/${patient.file}`, { responseType: 'arraybuffer' });
      console.log("Response received", response);
  
      // Ensuring the response has a content-type of PDF
      if (response.headers['content-type'] !== 'application/pdf') {
        throw new Error('Response is not a PDF file');
      }
  
      // Creating a binary data array
      const binaryData = new Uint8Array(response.data);
     
  
      // Creating a blob from the binary data
      const blob = new Blob([binaryData], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      
  
      // Opening the URL in a new tab
      window.open(url, '_blank', 'noopener,noreferrer');
    } catch (error) {
      console.error("Error fetching PDF file:", error);
    }
  };

 



  return (
    <div className="bg-gradient-to-br from-blue-50 to-indigo-100 min-h-screen">
      <Header />
      <div className="container mx-auto px-4 py-12 animate-fadeIn">
        {patient ? (
          <div className="bg-white shadow-2xl rounded-2xl overflow-hidden border-2 border-indigo-200 transition-all duration-500 ease-in-out hover:shadow-3xl">
            <div className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white py-8 px-8">
              <h2 className="text-4xl font-bold text-center animate-pulse">Medical Report Analysis</h2>
            </div>
            <div className="p-8 space-y-8">
              <div className="flex flex-wrap -mx-4 mb-8">
                {[
                  { label: "Report ID", value: patient._id },
                  { label: "Date of Report", value: patient.dateOfReport ? new Date(patient.dateOfReport).toLocaleDateString() : 'N/A' },
                  { label: "Patient Name", value: patient.patient },
                  { label: "Severity Level", value: patient.severity }
                ].map((item, index) => (
                  <div key={index} className="w-full sm:w-1/2 lg:w-1/4 px-4 mb-4 flex">
                    <div className="flex-1 border-2 border-indigo-100 rounded-lg p-4 bg-gradient-to-r from-gray-50 to-blue-50 hover:from-blue-50 hover:to-indigo-50 transition-colors duration-300 ease-in-out transform hover:-translate-y-1 hover:shadow-md flex flex-col justify-between">
                      <p className="text-indigo-600 text-sm font-semibold">{item.label}</p>
                      <p className="font-bold text-lg text-gray-800 mt-2">{item.value}</p>
                    </div>
                  </div>
                ))}
              </div>
              
              <div className="border-t-2 border-indigo-100 pt-8 animate-slideInFromLeft">
                <h3 className="text-2xl font-semibold mb-3 text-indigo-700">Summary</h3>
                <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-6 rounded-lg border-2 border-blue-200 shadow-inner">
                  <p className="text-gray-800">{patient.summary}</p>
                </div>
              </div>
              
              <div className="flex flex-wrap -mx-4 mb-8">
                <div className="w-full md:w-1/2 px-4 mb-4 animate-slideInFromLeft">
                  <h3 className="text-2xl font-semibold mb-3 text-indigo-700">Specialist Required</h3>
                  <div className="bg-gradient-to-r from-green-50 to-blue-50 p-6 rounded-lg border-2 border-green-200 font-medium h-full flex items-center justify-center">
                    <p className="text-green-700 font-bold">{patient.specialistReq}</p>
                  </div>
                </div>
                <div className="w-full md:w-1/2 px-4 mb-4 animate-slideInFromRight">
                  <h3 className="text-2xl font-semibold mb-3 text-indigo-700">Precautions</h3>
                  <ul className="list-disc list-inside bg-gradient-to-r from-yellow-50 to-orange-50 p-6 rounded-lg border-2 border-yellow-200 h-full">
                    {patient.precautions.map((precaution, index) => (
                      <li key={index} className="mb-2 text-gray-800">{precaution}</li>
                    ))}
                  </ul>
                </div>
              </div>
              
              <div className="animate-slideInFromBottom">
                <h3 className="text-2xl font-semibold mb-3 text-indigo-700">Possible Diseases</h3>
                <div className="flex flex-wrap -mx-2">
                  {patient.possibleDiseases.map((disease, index) => (
                    <div key={index} className="w-full sm:w-1/2 md:w-1/3 lg:w-1/4 px-2 mb-4">
                      <div className="bg-gradient-to-r from-red-50 to-pink-50 p-4 rounded-lg border-2 border-red-200 h-full flex items-center justify-center">
                        <span className="text-red-800 text-sm font-medium">
                          {disease}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
              
              <div className="animate-slideInFromBottom">
                <h3 className="text-2xl font-semibold mb-3 text-indigo-700">Doctor's Note</h3>
                <div className="bg-gradient-to-r from-purple-50 to-indigo-50 p-6 rounded-lg border-2 border-purple-200 italic shadow-inner">
                  <p className="text-gray-800">{patient.doctorNotes}</p>
                </div>
              </div>

              <div className="flex justify-center space-x-6 mt-10">
                <button onClick={generatePDF} className="px-8 py-3 bg-gradient-to-r from-blue-600 to-indigo-600 text-white font-semibold rounded-full hover:from-blue-700 hover:to-indigo-700 transition duration-300 shadow-md hover:shadow-lg transform hover:-translate-y-1">
                  Download Report
                </button>
                <button onClick={handlePDFView} className="px-8 py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-semibold rounded-full hover:from-indigo-700 hover:to-purple-700 transition duration-300 shadow-md hover:shadow-lg transform hover:-translate-y-1">
                  View Report
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="text-center py-20">
            <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
            <p className="text-xl text-gray-600 mt-4">Loading report details...</p>
          </div>
        )}
      </div>
    </div>
  );
}
