import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';

const Login = ({ setIsDoctor }) => {
  const [userRole, setUserRole] = useState("");
  const navigate = useNavigate();

  const handleValueChange = (event) => {
    const role = event.target.value;
    setUserRole(role);
    setIsDoctor(role === 'doctor');
  };

  const handleLogin = () => {
    if (userRole) {
      navigate("/main");
    } else {
      alert("Please select a role before logging in.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        {/* Left side - Form */}
        <div className="login-form">
          <h2>Welcome to Saanjh Sahayak</h2>
          <p>Please select your role to login.</p>
          
          <div className="form-group">
            <select
              value={userRole}
              onChange={handleValueChange}
            >
              <option value="" disabled>Select your role</option>
              <option value="doctor">Doctor</option>
              <option value="caretaker">Caretaker</option>
            </select>

            <button
              className={userRole ? 'active' : 'inactive'}
              onClick={handleLogin}
              disabled={!userRole}
            >
              Login
            </button>
          </div>
        </div>

        {/* Right side - Animated Design */}
        <div className="login-design">
          <div className="design-content">
            <h1>Saanjh Sahayak</h1>
            <p>Your Healthcare Companion</p>
          </div>
          
          {/* Animated circles */}
          <div className="animated-circle circle1"></div>
          <div className="animated-circle circle2"></div>
          
          {/* Animated healthcare icon */}
          <svg className="healthcare-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
          </svg>
        </div>
      </div>
    </div>
  );
};

export default Login;
