import React, { useState, useEffect } from 'react';
// import MyFrontpage from './frontpage';
import Doctor from './Doctors';
import {  useNavigate } from 'react-router-dom';
// import ViewPatient from './ViewPatients';
// import axios from 'axios';
// import { useParams } from 'react-router-dom';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// import { faFacebook } from '@fortawesome/free-brands-svg-icons';


const Header = () => {
    const Navigate= useNavigate();
    const handleCaretaker =()=>{
        Navigate("/Caretakers")
    }
    const handleHome =() =>{
        Navigate('/')
    }
    const handleDoctors =()=>{
        Navigate("/Doctors")
    }
    const [showNav, setShowNav] = useState(false);

    const toggleNav = () => {
        setShowNav(!showNav);
    };

    const [isScrolled, setIsScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const scrollTop = window.pageYOffset;

                  const threshold = 50;

            
            setIsScrolled(scrollTop > threshold);
        };

        // Add scroll event listener
        window.addEventListener('scroll', handleScroll);

        // Clean up the event listener on component unmount
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    return <div className={`saanjh ${isScrolled ? 'scrolled' : ''}`}>
        <div className='project'>
            <div>
        <header>
                <div className={`headerchange ${isScrolled ? 'scrolled' : ''}`}>
                <div className='mainheader'>
                    <div className='newthing'>
                    <div className='projectname'>
                        <img></img>
                        <h2>Saanjh Sahayak</h2>

                    </div>
                    <div className='symbol' onClick={toggleNav}>
                    <span >&#9776;</span>
                    </div>
                    </div>
                    
                    <div className={`navigation ${showNav ? 'show' : ''}`}>
                        <nav className='navbar'>
                            <div className='navbar-child'>
                                <ul className='navbar-elements'>
                                    <li onClick={handleHome}>
                                        <a>Home</a>
                                    </li>
                                    
                                    <li onClick={handleCaretaker}>
                                        <a>Caretaker</a>
                                    </li>
                                    <li onClick={handleDoctors}>
                                        <a>Doctor</a>
                                    </li>
                                    <li>
                                        <a>About Us</a>
                                    </li>
                                </ul>
                            </div>
                            <div className='joinin'>
                                <p>Join Us</p>

                            </div>


                        </nav>
                    </div>
                </div>
                </div>
            </header>
            </div>
            {/* {ViewPatient} */}
          
        </div>

    </div>;
}



export default Header;
