import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './Header';
import { useNavigate } from 'react-router-dom';

function Doctor() {
    const Navigate = useNavigate();
    const [patientList, setPatientList] = useState(null);

    useEffect(() => {
        const fetchPatients = async () => {
            const response = await axios.get("http://localhost:3001/en/getpatients");
            setPatientList(response.data);
        }
        fetchPatients();
    }, []);

    const handleUpload = () => {
        Navigate('/Upload');
    }

    const handleChatbotClick = () => {
        Navigate('/chatbot');
    };

    const handlePatientClick = (id) => {
        Navigate(`/doctorprofile/${id}`);
    };

    function formatDate(inputDate) {
        const date = new Date(inputDate);
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const year = date.getFullYear();
        return `${day}/${month}/${year}`;
    }

    return (
        <div>
            <Header />
            <div className='doctor-page'>
                <div className='patient-grid'>
                    {patientList && patientList.map((patient) => (
                        <div key={patient._id} className='patient-card' onClick={() => handlePatientClick(patient._id)}>
                            <h3>{patient.name}</h3>
                            <div className='patient-info'>
                                <p><strong>ID:</strong> {patient._id}</p>
                                <p><strong>Phone:</strong> {patient.phone}</p>
                                <p><strong>DOB:</strong> {formatDate(patient.DOB)}</p>
                                <p><strong>Gender:</strong> {patient.gender}</p>
                                <p><strong>Blood Group:</strong> {patient.bloodGroup}</p>
                            </div>
                        </div>
                    ))}
                </div>
                <div className='action-buttons'>
                    <button className='action-button' onClick={handleUpload}>Upload</button>
                    <button className='action-button chatbot-button' onClick={handleChatbotClick}>Open Chatbot</button>
                </div>
            </div>
        </div>
    );
}

export default Doctor;
