import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Header from './Header';

export default function AnalysisDoctor() {
  const [patient, setPatient] = useState(null);
  const [doctorNotes, setDoctorNotes] = useState('');
  const [isEditing, setIsEditing] = useState(false);

  const { id } = useParams();

  useEffect(() => {
    const getReportDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/en/getreportdetails?id=${id}`);
        setPatient(response.data);
        setDoctorNotes(response.data.doctorNotes || '');
      } catch (error) {
        console.error('Error fetching report details:', error);
      }
    };

    getReportDetails();
  }, [id]);

  const handlePDFView = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/en/files/${patient.file}`, { responseType: 'arraybuffer' });
      if (response.headers['content-type'] !== 'application/pdf') {
        throw new Error('Response is not a PDF file');
      }
      const binaryData = new Uint8Array(response.data);
      const blob = new Blob([binaryData], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      window.open(url, '_blank', 'noopener,noreferrer');
    } catch (error) {
      console.error("Error fetching PDF file:", error);
    }
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = async () => {
    try {
      await axios.post(`http://localhost:3001/en/updateDoctorNotes/${id}`, { doctorNotes });
      setIsEditing(false);
      alert("Doctor notes updated successfully.");
    } catch (error) {
      console.error('Error updating doctor notes:', error);
    }
  };

  const handleCancel = () => {
    setDoctorNotes(patient.doctorNotes || '');
    setIsEditing(false);
  };

  const handleChange = (e) => {
    setDoctorNotes(e.target.value);
  };

  return (
    <div className="analysis-doctor">
      <Header />
      <div className="analysis-doctor-container">
        {patient ? (
          <div className="analysis-doctor-content">
            <h3 className="analysis-doctor-header">REPORT DETAILS</h3>
            <div className="doctor-info-grid">
              <div className="doctor-info-item"><strong>ID:</strong> {patient._id}</div>
              <div className="doctor-info-item"><strong>Date of Report:</strong> {patient.dateOfReport ? new Date(patient.dateOfReport).toLocaleDateString() : 'N/A'}</div>
              <div className="doctor-info-item"><strong>Patient:</strong> {patient.patient}</div>
              <div className="doctor-info-item"><strong>Severity:</strong> {patient.severity}</div>
            </div>
            
            <div className="doctor-summary">
              <h4 className="doctor-section-title">Summary</h4>
              <p>{patient.summary}</p>
            </div>
            
            <div className="doctor-specialist">
              <h4 className="doctor-section-title">Specialist Required</h4>
              <p>{patient.specialistReq}</p>
            </div>
            
            <div className="doctor-precautions">
              <h4 className="doctor-section-title">Precautions</h4>
              <ul className="doctor-list">
                {patient.precautions.map((condition, index) => (
                  <li key={index} className="doctor-list-item">{condition}</li>
                ))}
              </ul>
            </div>
            
            <div className="doctor-diseases">
              <h4 className="doctor-section-title">Possible Diseases</h4>
              <ul className="doctor-list">
                {patient.possibleDiseases.map((condition, index) => (
                  <li key={index} className="doctor-list-item">{condition}</li>
                ))}
              </ul>
            </div>

            <div className="doctor-notes">
              <h4 className="doctor-section-title">Doctor's Notes</h4>
              {isEditing ? (
                <textarea
                  value={doctorNotes}
                  onChange={handleChange}
                  className="doctor-notes-textarea"
                />
              ) : (
                <p>{doctorNotes}</p>
              )}
            </div>

            <div className="doctor-action-buttons">
              {isEditing ? (
                <>
                  <button onClick={handleSave} className="doctor-button">Save</button>
                  <button onClick={handleCancel} className="doctor-button doctor-button-secondary">Cancel</button>
                </>
              ) : (
                <>
                  <button onClick={handleEdit} className="doctor-button">Edit Doctor's Notes</button>
                  <button onClick={handlePDFView} className="doctor-button doctor-button-secondary">View Report</button>
                </>
              )}
            </div>
          </div>
        ) : (
          <p>Loading report details...</p>
        )}
      </div>
    </div>
  );
}
