import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate} from 'react-router-dom';
// import Navigationvar from "./Navigationvar";
// import bot from '../assets/chat-bot-logo-design-concept-600nw-1938811039.webp';
import Header from './Header';

function Newuser() {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [role, setRole] = useState('doctor'); // Default role can be 'doctor'
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const handleChatbotClick = async () => {
    navigate('/chatbot');
  };

  const handleSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:3001/en/newuser', { email, name, role });
      setMessage('Signup successful! Please check your email to set your password.');
      // Redirect to login page
    } catch (error) {
      setMessage('An error occurred.');
      console.error('Error:', error);
    }
  };

  const formStyle = {
    width: '300px',
    margin: '50px auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center'
  };

  const inputStyle = {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    boxSizing: 'border-box'
  };

  const buttonStyle = {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    backgroundColor: '#990011FF',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  };

  return (
    <div><Header/>
    <div style={{ textAlign: 'center' }}>
      <h3 style={{marginTop:'6%'}}>Signup</h3>
      <div style={formStyle}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={inputStyle}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={inputStyle}
        />
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          style={inputStyle}
        >
          <option value="doctor">Doctor</option>
          <option value="caretaker">Caretaker</option>
        </select>
        <button style={buttonStyle} onClick={handleSubmit}>
          Signup
        </button>
        {message && <p>{message}</p>}
      </div>
      <div style={{ position: 'fixed', bottom: '20px', right: '20px', cursor: 'pointer' }} onClick={handleChatbotClick}>
        <img src={bot} alt="Chatbot Icon" style={{ width: '50px', height: '50px' }} />
      </div>
    </div>
    </div>
  );
}

export default Newuser;
